package Selenium;

public class switc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String day="tuesday";
		
		switch(day)
		{
		case "sunday":
			System.out.println("sunday");
			break;
			
		case "monday":
			System.out.println("monday");
			break;
		default:
			System.out.println("nil");
			break;
		}

	}

}
